(61, '{\"choices\":[{\"content\":\"A table that is currently being modified by a DML statement like defining triggers in a table is known as a Mutating table.\nA table that might need to be read from for a referential integrity constraint is known as constraining table\",\"isCorrect\":true,\"key\":\"A\"},{\"content\":\"A table that is not currently being modified by a DML statement like defining triggers in a table is known as a Mutating table.\nA table that might not need to be read from for a referential integrity constraint is known as constraining table\",\"isCorrect\":false,\"key\":\"B\"}],\"category\":\"Programming\",\"skill\":\"PLSQL\",\"question\":\"Mutating & Constraining Tables\",\"description\":\"What is the mutating table and constraining table?\",\"minimumExperience\":\"1\",\"maximumExperience\":\"10\",\"score\":\"10\",\"correctAnswer\":\"A\",\"addedBy\":\"encasherr@gmail.com\"}', 'PLSQL', 'Programming', NULL, NULL, '2020-10-05 08:37:38', '2'),
(60, '{\"choices\":[{\"content\":\"Closing a cursor jams the private SQL area as well as allocates memory\",\"isCorrect\":false,\"key\":\"A\"},{\"content\":\"Closing a cursor clears the private SQL area as well as de-allocates memory\",\"isCorrect\":true,\"key\":\"B\"}],\"category\":\"Programming\",\"skill\":\"PLSQL\",\"question\":\"Close Cursor\",\"description\":\"What does closing a cursor do?\",\"minimumExperience\":\"1\",\"maximumExperience\":\"10\",\"score\":\"10\",\"correctAnswer\":\"B\",\"addedBy\":\"encasherr@gmail.com\"}', 'PLSQL', 'Programming', NULL, NULL, '2020-10-05 08:37:38', '2'),
