(53, '{\"choices\":[{\"content\":\"% ROWTYPE is used when a query returns an entire row of a table or view.\nTYPE RECORD, on the other hand, is used when a query returns column of different tables or views.\",\"isCorrect\":true,\"key\":\"A\"},{\"content\":\"% ROWTYPE is used when a query does not return an entire row of a table or view.\nTYPE RECORD, on the other hand, is used when a query does not return column of different tables or views.\",\"isCorrect\":false,\"key\":\"B\"}],\"category\":\"Programming\",\"skill\":\"PLSQL\",\"question\":\"ROWTYPE and TYPE RECORD\",\"description\":\"Differentiate between % ROWTYPE and TYPE RECORD.\",\"minimumExperience\":\"1\",\"maximumExperience\":\"10\",\"score\":\"10\",\"correctAnswer\":\"A\",\"addedBy\":\"encasherr@gmail.com\"}', 'PLSQL', 'Programming', NULL, NULL, '2020-10-05 08:37:38', '2'),
(103, '{\"question\":\"NULL Comparisons\",\"description\":\"<p>What are the rules to be applied to NULLs whilst doing comparisons?</p>\n\",\"category\":\"Programming\",\"skill\":\"PLSQL\",\"minimumExperience\":1,\"maximumExperience\":10,\"choices\":[{\"content\":\"<ul>\n	<li>NULL is always TRUE or FALSE</li>\n	<li>NULL can be equal or unequal to other values</li>\n	<li>If a value in an expression is NULL, then the expression itself does not evaluates to NULL except for concatenation operator (||)</li>\n</ul>\n\",\"isCorrect\":false,\"key\":\"A\"},{\"content\":\"<ul>\n	<li>NULL is never TRUE or FALSE&nbsp;</li>\n	<li>NULL cannot be equal or unequal to other values</li>\n	<li>If a value in an expression is NULL, then the expression itself evaluates to NULL except for concatenation operator (||)</li>\n</ul>\n\",\"isCorrect\":true,\"key\":\"B\"}],\"score\":\"10\",\"createdBy\":2,\"createdOn\":\"2/24/2021\"}', 'PLSQL', 'Programming', NULL, NULL, '2021-02-24 12:09:50', '2'),
